import React, { useRef, useState } from 'react';
import BaseService from '../../services/baseService';
import API from '../../config/rest';
import './style.css';
import { setCookie } from '../../utils/cookie';

const UploadFile = () => {
    const [file, setFile] = useState('');
    const [data, getFile] = useState({ path: "", data: ""});
    const [isLoading, setLoading] = useState(false);
    const el = useRef();

    const handleChange = (e) => {
        setLoading(false)
        const file = e.target.files[0];
        console.log(file);
        setFile(file);
    }

    const uploadFile = () => {
        const formData = new FormData();
        formData.append("file", file);

        setLoading(true);

        BaseService.post(API.UPLOAD, formData, (res) => {
            console.log(res);
            getFile({
                path: res.data.path,
                data: res.data.data
            })
        })
        .finally(() => {
            setLoading(false);
            // window.location.replace('/uploads');
        }) 
    }

    return(
        <div>
            <div className="file-upload">
                <input type="file" id="file" name="file" accept="application/pdf" required ref={el} onChange={handleChange} />
            </div>
            <button onClick={uploadFile} className="btn btn-success upbutton" disabled={isLoading}>UPLOAD</button>
        </div>
    );
}

export default UploadFile;
