import React, { useEffect, useState } from 'react';
import './Home.css';
import { Button, Form, FormGroup, Input } from 'reactstrap';
import { setCookie } from '../../utils/cookie';
import { uploads } from '../../services';

const Home = () => {
    const [file, setFile] = () => {
        setState('');
    };
    const [isLoading, setLoading] = useState(false);

    const selectFile =(event) => {
        this.setState({
          selectedFiles: event.target.files,
        });
      }

    const onSubmit = () => {
        setLoading(true);
        uploads.uploads(file).then((res) => {
            const cookieName = res.path;
            const cookieData = res.data;

            setCookie('path', JSON.stringify(cookieName), 1000);
            setCookie('data', JSON.stringify(cookieData), 1000);
        })
        .finally(() => {
            setLoading(false);
            window.location.replace('/uploads')
        })
    }
    return (
        <div className="container">
            <div className="body-part">
                <h1>PLAGIARISM CHECKER</h1>
                <p>We are an Anti-Cheating Squad Diploma of Telecommunication Technology Be Aware!!</p>
                <Form>
                    <FormGroup>
                        <label>Upload PDF Document</label>
                        <br />
                        <input className="file-upload" type="file" id="file" name="file" accept="application/pdf" required onChange={(e) => {setFile(e.target.value);}} />
                        {/* <Input align="center" type="file" id="file" name="file" accept="application/pdf" required onChange={(e) => {setFile(e.target.value);}} /> */}
                    </FormGroup>
                    <div className="button">
                        <Button color="success" onClick={(e) => {
                            e.preventDefault();
                            onSubmit();
                        }} disabled={isLoading}>Submit</Button>
                    </div>
                </Form>
                {/* <form className="abc" action="/upload" method="POST" encType="multipart/form-data">
                    <label for="avatar" className="btn-get-started"><i className="fa fa-upload"></i> Upload PDF Document</label>
                    <br></br>
                    <div className="inputWraper">
                        <input className="file-upload" type="file" id="file" name="file" accept="application/pdf" required />
                    </div>
                    <br />
                    <input className="btn btn-success" type="submit" id="submit-btn" name="submit"></input>
                </form> */}
            </div>
        </div>
    )
}

export default Home;
