import React, { useRef, useState } from 'react';
import BaseService from '../../services/baseService';
import API from '../../config/rest';
import './style.css';

const UploadFile = () => {
    const [file, setFile] = useState('');
    const [data, setData] = useState({ path: '' });
    const [isLoading, setLoading] = useState(false);
    const el = useRef();

    const handleChange = (e) => {
        setLoading(false)
        const file = e.target.files[0];
        console.log(file);
        setFile(file);
    }

    const dataPath = data.path;

    const uploadFile = () => {
        const formData = new FormData();
        formData.append("file", file);

        setLoading(true);

        BaseService.post(API.UPLOAD, formData, (res) => {
            console.log(res.data);
            // res.JSON({
            //     path: res.data.path,
            //     data: res.data.data
            // })
            setData({
                path: res.data.path,
                // data: res.data.data
            })
            // setLoading(false);
        })
        // .then(
        //     res => {
        //         res.JSON()
        //     },
        //     error => console.log('An error occurred.', error)
        // )
        .finally(() => {
            setLoading(false);
            window.location.replace(`/uploads/${data.path}`);
        }) 
    }

    return(
        <div>
            <div className="body-part">
                <h1>PLAGIARISM CHECKER</h1>
                <p>We are an Anti-Cheating Squad Diploma of Telecommunication Technology Be Aware!!</p>
                <div className="file-upload">
                    <input type="file" id="file" name="file" accept="application/pdf" ref={el} onChange={handleChange} required />
                </div>
                <button onClick={uploadFile} className="btn btn-success upbutton" disabled={isLoading}>UPLOAD</button>
            </div>
        </div>
    );
}

export default UploadFile;
