import React, { useEffect, useState } from 'react';
import { Container, Card } from 'react-bootstrap';
import { appServices } from '../../services';
import './final_project.css';
import { Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';
import { Button, FormGroup } from 'react-bootstrap';
import pdffile from '../../img/pdflogo.png'

const Final_Project = () => {
    const [modalDetail, setModalDetail] = useState(false);
    const [dataFinal, setData] = useState([]);

    useEffect(() => {
        appServices.getAllData().then((result) => {
            setData(result.data);
        });
    }, []);

    const [detail, setDetail] = useState('');
    // useEffect((dataFinal, id) => {
    //     appServices.getDataId(id).then((result) => {
    //         setDetail(result.data);
    //     });
    // });

    const toggleDetail = (event) => {
        const id = event.target.id
        // appServices.getDataId(dataFinal.id).then((result) => {
        //     setDetail(result.data);
        // })
        return setModalDetail(!modalDetail);
    }

    return (
        <div className="finalBody">
            <h1>OUR PROUD PROJECT</h1>
            <Container>
                <div>
                    <input className="search" type="text" placeholder="Search"></input>
                </div>
                <Card className="cardData" style={{width:'100%', height:'100%', color:'black', top:'20px'}}>
                    <Card.Body className="cardBody">
                        <div className="row" align="left">
                            <div className="col-sm-1" />
                            <div className="col-sm-10">
                                <table>
                                    <tr>
                                        <th width="50%">Title</th>
                                        <th width="35%">Author</th>
                                        <th width="15%">Year</th>
                                        <th></th>
                                    </tr>
                                    {dataFinal.map((data) => {
                                        return (
                                            <tr>
                                                <td>{data.title}</td>
                                                <td>{data.author}</td>
                                                <td>{data.year}</td>
                                                {/* <td> */}
                                                    <Button className="btnDetail" id={data.id} variant="outline" onClick={toggleDetail}>Detail</Button>
                                                {/* </td> */}
                                            </tr>
                                        )
                                    })}
                                </table>
                            </div>
                        </div>
                    </Card.Body>
                </Card>
                <div>
                    <Modal isOpen={modalDetail} toggle={toggleDetail} className="modal-xl">
                        <ModalHeader toogle={toggleDetail}>
                            <b>Detail</b>
                        </ModalHeader>
                        <ModalBody>
                            <FormGroup>
                                <div className="container">
                                    <div className="row">
                                        <div className="col-sm-3">
                                            <img className="pdflogo" alt="pdflogo" src={pdffile} style={{height:'300px', width:'300px'}}></img>
                                        </div>
                                        <div className="col-sm-9">
                                            <table>
                                                <tr>
                                                    <td style={{width:'20%'}}><h6>Title</h6></td>
                                                    <td><h6>:</h6></td>
                                                    <td style={{width:'100%'}}>
                                                        <h6></h6>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td><h6>Author</h6></td>
                                                    <td><h6>:</h6></td>
                                                    <td>
                                                        <h6>Data</h6>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td><h6>NIM</h6></td>
                                                    <td><h6>:</h6></td>
                                                    <td>
                                                        <h6>Data</h6>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td><h6>Lecture 1</h6></td>
                                                    <td><h6>:</h6></td>
                                                    <td>
                                                        <h6>Data</h6>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td><h6>Lecture 2</h6></td>
                                                    <td><h6>:</h6></td>
                                                    <td>
                                                        <h6>Data</h6>
                                                    </td>
                                                </tr>
                                                <tr>
                                                    <td><h6>Year</h6></td>
                                                    <td><h6>:</h6></td>
                                                    <td>
                                                        <h6>Data</h6>
                                                    </td>
                                                </tr>
                                            </table>
                                        </div>
                                    </div>
                                </div>
                            </FormGroup>
                        </ModalBody>
                        <ModalFooter className="footer">
                            <Button>Download</Button>
                            <Button onClick={toggleDetail} variant="outline-secondary">
                                <i className="fa fa-times" aria-hidden="true">
                                    {' '}
                                    Cancel
                                </i>
                            </Button>
                        </ModalFooter>
                    </Modal>
                </div>
            </Container>
        </div>
    )
}

export default Final_Project;
